import React from "react";
import "./Contact.css";
import Container from "@material-ui/core/Container";
import emailjs from "emailjs-com";
import { Form, Col, Button } from "react-bootstrap";
import dotenv from "dotenv";

export default function Contact() {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm(
        process.env.SERVICE_ID,
        process.env.TEMPLATE_ID,
        e.target,
        process.env.USER_ID
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  }

  return (
    <div className="contactComponent">
      <Container className="footerContainer">
        <Form onSubmit={sendEmail}>
          <Form.Row>
            <Form.Group as={Col}>
              <Form.Control
                type="text"
                placeholder="Name"
                name="name"
                required
              />
            </Form.Group>

            <Form.Group as={Col}>
              <Form.Control
                type="email"
                placeholder="Email"
                name="email"
                required
              />
            </Form.Group>
          </Form.Row>

          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Subject"
              name="subject"
              required
            />
          </Form.Group>

          <Form.Group>
            <Form.Control
              as="textarea"
              rows={7}
              type="text"
              placeholder="Message"
              name="message"
              required
            />
          </Form.Group>

          <Button type="submit" variant="primary">
            Send message
          </Button>
        </Form>
      </Container>
    </div>
  );
}
